package com.common;import android.app.Application;import com.common.bean.LoginBean;public abstract class BaseApp  extends Application {	public static BaseApp app = null;			@Override	public void onCreate() {		super.onCreate();		app = this;	}		public static BaseApp getApp() {		return app;	}		public abstract  LoginBean getLoginBean();	public abstract void setLoginBean(LoginBean loginBena);}